import React from 'react';
import Todos from './Components/Todos';
class App extends React.Component {
  state = {
    todos: [
      {
        id: 1,
        title: 'Take out the bash',
        completed: true
      },
      {
        id: 2,
        title: 'Bools',
        completed: false
      },
      {
        id: 3,
        title: 'Stools',
        completed: false
      },
    ]
  }
// Toggle Complete
  markCompletedFinal = (id) => {
    this.setState({
      todos: this.state.todos.map(todo => {
        if(todo.id === id) {
          todo.completed = !todo.completed
        }
        return todo
      })
    })
  }
  render() {
      return (
    <div className="App">
     <Todos farts={this.state.todos} markCompleted = {this.markCompletedFinal}/>
    </div>
  );
}
};

export default App;
