import React from 'react';
import Header from './Components/Layout/Header.js'
import Todos from './Components/Todos';
import AddTodo from './Components/AddTodo';

import './App.css'


class App extends React.Component {
  state = {
    todos: [
      {
        id: 1,
        title: 'Take out the bash',
        completed: true
      },
      {
        id: 2,
        title: 'Bools',
        completed: false
      },
      {
        id: 3,
        title: 'Stools',
        completed: false
      },
    ]
  }
// Toggle Complete
  markCompletedFinal = (id) => {
    this.setState({
      todos: this.state.todos.map(todo => {
        if(todo.id === id) {
          todo.completed = !todo.completed
        }
        return todo
      })
    })
  }

  delTodo = (id) => {
    this.setState({
      todos: [...this.state.todos.filter(todo => todo.id !== id)]
    });
  }
  render() {
      return (
    <div className="App">
      <div className = 'container'>
        <Header />
        <AddTodo />
        <Todos farts={this.state.todos} markCompleted = {this.markCompletedFinal} delTodos = {this.delTodo}/>
      </div>
    </div>
  );
}
};

export default App;
