import React from 'react';
import {BrowserRouter as Router, Route} from 'react-router-dom';
import Header from './Components/Layout/Header.js'
import Todos from './Components/Todos';
import AddTodo from './Components/AddTodo';
import uuid from 'uuid';

import './App.css'


class App extends React.Component {
  state = {
    todos: [
      {
        id: uuid.v4(),
        title: 'Enhance front end',
        completed: false
      },
      {
        id: uuid.v4(),
        title: 'Refine existing NoSQL database',
        completed: false
      },
      {
        id: uuid.v4(),
        title: 'Migrate to Neo4j graph database',
        completed: false
      },
    ]
  }
// Toggle Complete
  markCompletedFinal = (id) => {
    this.setState({
      todos: this.state.todos.map(todo => {
        if(todo.id === id) {
          todo.completed = !todo.completed
        }
        return todo
      })
    })
  }

  delTodo = (id) => {
    this.setState({
      todos: [...this.state.todos.filter(todo => todo.id !== id)]
    });
  }

  addToDo = (title) => {
    const newTodo = {
      id:uuid.v4(),
      title: title,
      completed: false
    }
    this.setState({todos:[...this.state.todos, newTodo]})
  }

  render() {
      return (
        <Router>
    <div className="App">
      <div className = 'container'>
        <Header />
        <AddTodo addToDo={this.addToDo}/>
        <Todos farts={this.state.todos} markCompleted = {this.markCompletedFinal} delTodos = {this.delTodo}/>
      </div>
    </div>
    </Router>
  );
}
};

export default App;
