import React, { Component } from 'react';
import PropTypes from 'prop-types';


export class TodoItem extends Component {
    getStyle = () => {
       return {
           background: '#f4f4f4',
           padding: '10px',
           borderBottom: '1px #ccc dotted',
           textDecoration: this.props.smarts.completed ? 'line-through' : 'none'
       }
    }

    render() {
        const {id, title} = this.props.smarts;
        return (
            <div style={this.getStyle()}>
                <p>
                    <input type = "checkbox" onChange={this.props.markComplete.bind(this, id)}/>
                {title}
                <button style={btnStyle}>x</button>
                </p>
            </div>
        )
    }
}

// PropTypes
TodoItem.propTypes = {
    smarts: PropTypes.object.isRequired
}

const btnStyle = {
    background: '#ff0000',
    color: '#fff',
    border: 'none',
    padding: ' 5px 8px',
    borderRadius: '50%',
    cursor: 'pointer',
    float: 'right'
}
export default TodoItem
