import React, { Component } from 'react';
import PropTypes from 'prop-types';


export class TodoItem extends Component {
    getStyle = () => {
       return {
           textDecoration: this.props.smarts.completed ? 'line-through' : 'none'
       }
    }
    render() {
        return (
            <div style={this.getStyle()}>
                <p>{this.props.smarts.title}</p>
            </div>
        )
    }
}

// PropTypes
TodoItem.propTypes = {
    smarts: PropTypes.object.isRequired
}

const itemStyle = {
    backgroundColor: '#f4f4f4'
}

export default TodoItem
