import React from 'react';
import {BrowserRouter as Router, Route} from 'react-router-dom';
import Header from './Components/Layout/Header.js'
import Todos from './Components/Todos';
import AddTodo from './Components/AddTodo';
import About from './Components/Pages/About';
import uuid from 'uuid';
import axios from 'axios';

import './App.css'


class App extends React.Component {
  state = {
    todos: [
    ]
  }

  componentDidMount() {
    axios.get('https://jsonplaceholder.com.typicode.com/todos?_limit=10').then(res => this.setState({todos:res.data}) )
  }
// Toggle Complete
  markCompletedFinal = (id) => {
    this.setState({
      todos: this.state.todos.map(todo => {
        if(todo.id === id) {
          todo.completed = !todo.completed
        }
        return todo
      })
    })
  }

  delTodo = (id) => {
    this.setState({
      todos: [...this.state.todos.filter(todo => todo.id !== id)]
    });
  }

  addToDo = (title) => {
    const newTodo = {
      id:uuid.v4(),
      title: title,
      completed: false
    }
    this.setState({todos:[...this.state.todos, newTodo]})
  }

  render() {
      return (
        <Router>
    <div className="App">
      <div className = 'container'>
        <Header />
        <Route exact path="/" render={props=>(
          <React.Fragment>
              <AddTodo addToDo={this.addToDo}/>
              <Todos farts={this.state.todos} markCompleted = {this.markCompletedFinal} delTodos = {this.delTodo}/>
          </React.Fragment>
        )} />
        <Route path="/about" component={About}/>

      </div>
    </div>
    </Router>
  );
}
};

export default App;
